mapper.select:
  select *
    from posts p,users u
   where status = 0 and p.author_id = u.id

     and p.id = #{id}


     and p.id = #{author_id}

order by p.create_time desc

mapper.update:
update posts
   set status      = #{status},
       update_time = #{updateTime}
 where id          = #{id}
